<?php   /*   Plugin Name: Simple Drop-ins   Plugin URI: http://wordpress.org/plugins/simple-drop-ins/   Description: This plugin is using API of Dropbox. You can choose your file and put the link of your file in your dropbox account into Wordress posting.    Version: 1.0.0   Author: Rosdyana Kusuma   Author URI: http://r3m1ck.us/   License: GPL2   *//** * Register a box to the main column on the Post and Page edit screens. */function simple_drop_ins_add_metabox() {    $screens = array( 'post', 'page' );    foreach ( $screens as $screen ) {        add_meta_box(            'simple_drop_ins_id',            'Simple Drop-ins',            'simple_drop_ins_custom_box',            $screen,			'normal'        );    }}add_action( 'add_meta_boxes', 'simple_drop_ins_add_metabox' );/** * Show metabox to Post and Page edit screens */function simple_drop_ins_custom_box() {	$html ='<script type="text/javascript" src="' . plugins_url( 'js/dropins.js' , __FILE__ ) . '" id="dropboxjs" data-app-key="znjed8hpce07ei1"></script>	<input type="dropbox-chooser" name="selected-file" data-link-type="direct" id="db-chooser"/>	<p>Your file link :</p>	<p id="links"></p>	<script type="text/javascript">    // add an event listener to a Chooser button    document.getElementById("db-chooser").addEventListener("DbxChooserSuccess",        function(e) {			var the_link = e.files[0].link;			document.getElementById("links").innerHTML=the_link.link(the_link);			window.prompt ("Copy to clipboard: Ctrl+A, Ctrl+C, Enter", "[button link="+the_link+"]"+the_link+"[/button]")        }, false);	</script>';	echo $html;}/***	Add shortcode to create link*/function sButton($atts, $content = null) {   extract(shortcode_atts(array('link' => '#'), $atts));   return '<a href="'.$link.'" target=_blank>' . do_shortcode($content) . '</a>';}add_shortcode('button', 'sButton');?>